	private EntityManager entityManager;

	private Random random = new Random();

	public DatabaseService(RestaurantRepository restaurantRepository, AvisRepository avisRepository, CategoriePlatRepository categoriePlatRepository, PlatRepository platRepository,EntityManager entityManager) {
		this.restaurantRepository = restaurantRepository;
		this.avisRepository = avisRepository;
		this.categoriePlatRepository = categoriePlatRepository;
		this.platRepository = platRepository;
		this.entityManager=entityManager;
	}


	public void clearDatabase() {
		logger.info("Début cleardatabase");
		avisRepository.deleteAll();
		logger.info("Fin suppression avis");
		platRepository.deleteAll();
		logger.info("Fin suppression plats");
		restaurantRepository.deleteAll();
		logger.info("Fin suppression restaurant");
		categoriePlatRepository.deleteAll();
		logger.info("Fin suppression catégorie");
		entityManager.createNativeQuery("DROP TABLE IF EXISTS tmp_restaurant_2019").executeUpdate();
		logger.info("Fin cleardatabase");
	}

	public void init() {
		int cpt=1;

		initCategoriePlat();

		for(String adj:adjectifs_smart){
			//FIXME pro for(String nom:noms){
			//Arrays.asList("abatteur")
			for(String nom:noms){
				//FIXME retirer trace sql profil docker, pb vegetratien aleatoire,
				Restaurant r = new Restaurant();
				r.setNom(adj + " " + nom);
				r.setAdresse("" + cpt);
				r.setVegetarien(random.nextInt(2) == 1 ? "OUI" : "NON");
				r.setDateCreation(LocalDateTime.of(2000+random.nextInt(23),1+random.nextInt(12),1+random.nextInt(23),0,0));
				// Paris, France lat : 48.8566d, long : 2.3522d
				r.setLa(48.8d+random.nextInt(100)*0.001);
				r.setLo(2.3d+random.nextInt(100)*0.001);
				cpt++;

				List<Plat> plats=createPlats();
				r.setPlats(plats);
				restaurantRepository.save(r);

				for(Plat plat:plats){
					plat.setRestaurant(r);
					platRepository.save(plat);
				}

				List<Avis> aviss=createAvis();
				r.setAvis(aviss);

				for(Avis avis:aviss){
					avis.setRestaurant(r);
					avisRepository.save(avis);
				}

				if (cpt % 1000 == 0) {
					entityManager.flush();
					entityManager.clear();
					logger.info("Nb restaurants créés {}",cpt);
				}
			}
			entityManager.flush();
			entityManager.clear();
		}
		logger.info("Tous les restaurants ont été créés. Nb {}",cpt);
		entityManager.createNativeQuery("create table tmp_restaurant_2019 as \n" +
				"select * \n" +
				"from restaurant r \n" +
				"where r.date_creation <'01/01/2020'").executeUpdate();
		logger.info("Fin création table tmp_restaurant_2019");
	}

	private List<Avis> createAvis() {
		List<Avis> aviss=new ArrayList<>();
		for(int i=0;i<random.nextInt(10);i++){
			Avis avis=new Avis();

			avis.setNote((long) (1+random.nextInt(5)));
			avis.setAuteur(prenoms.get(random.nextInt(prenoms.size())));

			if(avis.getNote()>2l){
				avis.setCommentaire(adjectifsPositifs.get(random.nextInt(adjectifsPositifs.size())));
			}
			else{
				avis.setCommentaire(adjectifsNegatifs.get(random.nextInt(adjectifsNegatifs.size())));
			}
			avis.setLieu("Paris");
			avis.setDateCreation(LocalDate.of(2016+random.nextInt(7), 1+random.nextInt(12), 1+random.nextInt(23)));

			aviss.add(avis);
		}
		return aviss;
	}

	private List<Plat> createPlats() {
		List<Plat> plats=new ArrayList<>();
		for(int i=0;i<random.nextInt(5);i++){
			Plat plat=new Plat();

			plat.setCategoriePlat(categoriePlatRepository.findById("PLA").get());
			plat.setLibelle(nomPlats.get(i));
			plat.setPrix(((double) (10+random.nextInt(10))+0.1d*random.nextInt(10)));
			plats.add(plat);
		}


		for(int i=0;i<random.nextInt(5);i++){
			Plat plat=new Plat();

			plat.setCategoriePlat(categoriePlatRepository.findById("DES").get());
			plat.setLibelle(nomDesserts.get(i));
			plat.setPrix(((double) (10+random.nextInt(5))+0.1d*random.nextInt(10)));
			plats.add(plat);
		}
		return plats;
	}

